import React from 'react';
import './Registration.css';
import data from './data.json';
import Header from "./Header.js";
import Radio from "@material-ui/core/Radio";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormLabel from "@material-ui/core/FormLabel";
import FormControl from "@material-ui/core/FormControl";
import FormHelperText from "@material-ui/core/FormHelperText";
import Button from "@material-ui/core/Button";
import RadioGroup from "@material-ui/core/RadioGroup";

class Registration extends React.Component {
  constructor(props) {
    super(props);
    this.state={
      currentPage:0,
      selected:[null,"","","",""],
      visitedPage:[true, false, false, false,false],
      answer:"",
    }
    this.sections = ["personal info","location","experience","wage","license"]
  }

  handleAnswer = (e) => {
    let val = e.target.value;
    let index = this.state.currentPage;
    this.setState({answer:val});
    const newSelected = this.state.selected;
    newSelected[index] = val;
    this.setState({selected:newSelected});
  }


  render() {
    let currVal = this.state.selected[this.state.currentPage];
    return (
      <div className="registration">
        <Header goToHome={this.goHome} goToLogin={this.goLogin} goToSignUp={this.goSignUp}/>
        <div>
          <div className="question">
            What is your area of expertise?
          </div>
          <FormControl component="fieldset">
            <RadioGroup aria-label="gender" name="gender1" value={currVal} onChange={this.handleAnswer}>
              <FormControlLabel value="female" control={<Radio color="primary"/>} label="Female" />
              <FormControlLabel value="male" control={<Radio color="primary"/>} label="Male" />
              <FormControlLabel value="other" control={<Radio color="primary"/>} label="Other" />
            </RadioGroup>
          </FormControl>
          {/* <RadioGroup aria-label="gender" name="gender1" value={currVal}> */}
            {/* <Radio value="female" checked={currVal==="female"} color="primary" onChange={this.handleAnswer}/>
            <Radio value="male" checked={currVal==="male"} color="primary" onChange={this.handleAnswer}/> */}
          {/* </RadioGroup> */}

        <div className="login-button-container">
          <div className="login-button">next</div>
        </div>
        </div>
      </div>
    );
  }
}

export default Registration;
