import React from 'react';
import './JobModal.css';

class JobCard extends React.Component {
    constructor(props) {
        super(props);
        this.state={
            handleClose: this.props.handleClose,
            children: this.props.children,

            // addingJob: this.props.addingJob,
            // removingJob: this.props.removingJob
        } 
        this.showHideClassName = this.showHideClassName.bind(this);

      }

      showHideClassName  = () => {
        if (this.props.show){
            return "modal display-block";
        } else {
            return "modal display-none";
        }
      };


  render() {


    return (
<div className={this.state.showHideClassName}>
      <section className="modal-main">
        {this.state.children}
        <button type="button" onClick={this.state.handleClose}>
          Close
        </button>
      </section>
    </div>

      );
  }
}

export default JobCard;
